    try {
      req.body.autor = req.user.id;
      const post = new Post(req.body);
      await post.adiciona();

      res.status(201).json(post);
    } catch (erro) {
      if (erro instanceof InvalidArgumentError) {
        return res.status(400).json({ erro: erro.message });
      }
      res.status(500).json({ erro: erro.message });
    }
